import React from 'react';
import logo from './logo.svg';
import './App.css';

class App extends React.Component {
    constructor(props) {
        super(props);
        this.state = { data: [] };
    }

    callAPI() {
        fetch("http://localhost:9000/getData")
            .then(res => res.json())
            .then(res => this.setState({ data: res.message }));
    }

    componentWillMount() {
        this.callAPI();
    }

    render() {
        return (
            <div className="App">
                <style>

                </style>
                <header className="nav-bar" position="relative" color="white">
                    <img src={logo} className="App-logo" alt="logo"  width={182} height={64}/>
                </header>
                <div style={{ display: 'inline', float: 'left' }}>
                    <ol style={{ listStyle: 'square' }}>
                        {this.state.data.map(function (d, idx) {
                            return (<li key={idx}>The data of id = {d.id} is as follows, <br></br>Name - {d.name} <br></br>Username - {d.username}</li>)
                        })}
                    </ol>
                </div>
            </div>
        );
    }
}

export default App;
