import React from 'react';
import logo from './logo.svg';
import './App.css';

class App extends React.Component {
    constructor(props) {
        super(props);
        this.state = {data : []};
    }

    callAPI() {
        fetch("http://localhost:9000/getData")
            .then(res => res.json())
            .then(res => this.setState({data : res.message}));
    }

    componentWillMount() {
        this.callAPI();
    }

    render() {
        return (
            <div className="App">
                <header className="App-header">
                    <img src={logo} className="App-logo" alt="logo" />
                </header>
                <div>
                    {this.state.data.map(function (d, idx) {
                        return (<li key={idx}>{`The data of id = {d.id} is as follows,\n
                        Name - {d.name} {"\n"}\n
                        Username - {d.username}`}</li>)
                    })}
                </div>
            </div>
        );
    }
}

export default App;
