import React from 'react';
import logo from './logo.svg';
import './App.css';

class App extends React.Component {
    constructor(props) {
        super(props);
        this.state = { data: [] };
    }

    callAPI() {
        fetch("http://localhost:9000/getData")
            .then(res => res.json())
            .then(res => this.setState({ data: res.message }));
    }

    componentWillMount() {
        this.callAPI();
    }

    render() {
        return (
            <div className="App">
                <style>
                    .higher {
                        listStyle = "square"
                    }

                    .lower {
                        listStyle = "bullet"
                    }
                </style>
                <header className="nav-bar" position="relative" color="white">
                    <img src={logo} className="App-logo" alt="logo" width={182} height={64} />
                </header>
                <div style={{ display: 'inline', float: 'left' }}>
                    <ol>
                        {this.state.data.map(function (d, idx) {
                            return (<li key={idx} class="higher">The data of id = {d.id} is as follows, <br></br>
                                <li key={d.name} class="lower">
                                    Name - {d.name} <br></br>
                                </li>
                                <li key={d.username} class="lower">
                                    Username - {d.username}
                                </li>
                            </li>)
                        })}
                    </ol>
                </div>
            </div >
        );
    }
}

export default App;
